import React, { useEffect, useState } from 'react';
import { sendInstruction } from './api';

const HomePage = () => {
  const [imageUrl, setImageUrl] = useState('');
  const [instruction, setInstruction] = useState('');

  const handleInstructionChange = (event) => {
    setInstruction(event.target.value);
  };

  const handleSendInstruction = async () => {
    try {
      // Get the instruction from the user input field or textarea
      const instructionTextArea = document.getElementById('instructionInput');
      const instruction = instructionTextArea.value;
  
      // Send the instruction to the backend
      const response = await fetch('http://localhost:4000/api/instructions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ instruction }),
      })
  } catch (error) {
    console.error(error);
  }
  }

  useEffect(() => {
    const imageElement = document.getElementById('Elephant');

    // Function to update the image source
    const updateImageSource = () => {
      fetch('http://localhost:4000/api/get_image_url')
        .then(response => response.json())
        .then(data => {
          const imageUrl = data.image_url;
          imageElement.src = imageUrl;
        })
        .catch(error => {
          console.error('Error:', error);
        });
    };

    // Refresh the image source every five seconds
    const intervalId = setInterval(updateImageSource, 500);

    // Cleanup function to clear the interval when the component is unmounted
    return () => {
      clearInterval(intervalId);
    };
  }, []); // Empty dependency array ensures the effect runs only once after initial render

  return (
    <div>
      <div className="header">Drop</div>
      <div className="logo">
        <img src="logo.png" alt="Logo" width="50" />
      </div>
      <ul className="menu">
        <li>
          <NavLink to="/">Home</NavLink>
        </li>
        <li>
          <NavLink to="/community">Community</NavLink>
        </li>
        <li>
          <a href="#">Style Collection</a>
        </li>
        <li>
          <a href="#">Support</a>
        </li>
      </ul>
      <div className="header">Styles</div>
      <div className="buttons">
        <button>Style1</button>
        <button>Style2</button>
      </div>
      <div className="header">Instructions</div>
      <textarea
        id="instructionInput"
        rows="4"
        cols="50"
        value={instruction}
        onChange={handleInstructionChange}
      ></textarea>
      <button onClick={handleSendInstruction}>Send Instruction</button>
      <img id="Elephant" src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/41/Siberischer_tiger_de_edit02.jpg/640px-Siberischer_tiger_de_edit02.jpg" alt="Elephant Image"></img>
      <div className="header">Gallery</div>
      <div className="gallery">
        <img src="image1.jpg" alt="Image 1" />
        <img src="image2.jpg" alt="Image 2" />
        <img src="image3.jpg" alt="Image 3" />
      </div>
    </div>
  );
};

export default HomePage;
