import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import './HomePage.css';
import './CommunityPage.css';

const CommunityPage = () => {
  const [images, setImages] = useState([]);
  const [selectedPhoto, setSelectedPhoto] = useState(null);

  const handlePhotoClick = (photo) => {
    setSelectedPhoto(photo);
  };

  useEffect(() => {
    const fetchImages = async () => {
      try {
        // Fetch the images with community = true from the backend API
        const response = await fetch('http://localhost:4000/api/images?community=true');
        const data = await response.json();

        // Update the images state with the fetched images
        setImages(data.images);
      } catch (error) {
        console.error(error);
      }
    };

    fetchImages();
  }, []);

  return (
    <div className="body-container">
      <div className="head-menu-container">
        <div className="head-container">
          <div className="logo-title-container">
            <div className="logo">
              <img src="Drop.jpg" alt="Logo" className="logo-image" />
            </div>
            <div className="head-title">DROP</div>
          </div>
        </div>
        <ul className="menu">
          <li>
            <NavLink to="/">Home</NavLink>
          </li>
          <li className="separator">|</li>
          <li>
            <NavLink to="/community">Community</NavLink>
          </li>
          <li className="separator">|</li>
          <li>
            <NavLink to="/styleCollection">Style Collection</NavLink>
          </li>
          <li className="separator">|</li>
          <li>
            <NavLink to="/support">Support</NavLink>
          </li>
        </ul>
      </div>
      <div className="header-gallery">Community</div>
      <div className="gallery">
        {images.map((image) => (
          <img
            key={image._id}
            src={image.imageUrl}
            alt={image.prompt}
            className={selectedPhoto === image._id ? 'selected' : ''}
            onClick={() => handlePhotoClick(image._id)}
          />
        ))}
      </div>
      {selectedPhoto && (
        <div className="selected-photo">
          <img src={selectedPhoto.imageUrl} alt="Selected Image" className="selected-image" />
          <div className="caption">{selectedPhoto.prompt}</div>
        </div>
      )}
    </div>
  );
};

export default CommunityPage;
