import axios from 'axios';

// Function to send instruction to the backend
export const sendInstruction = async (instruction) => {
  try {
    const response = await axios.post('http://localhost:4000/api/instructions', { instruction });
    return response.data;
  } catch (error) {
    throw new Error('Failed to send instruction');
  }
};

// Function to update the photo
export const getUpdate = async() => {
    try {
        console.log("Getting update");
        const response = await axios.get('http://localhost:4000/getupdate')
        console.log(response.data);
        return response.data      
    } catch (error) {
        throw new Error('Failed to update');
    }
};
