        <div className="parametres">
          <div className="dimension-parametre">
            <label htmlFor="dimension">
              <FaRulerCombined size={20} style={{ marginRight: '10px' }} />
              Dimensions
            </label>
            <div className="dimension-icons">
              <FaImage size={20} />
              <input
                key={imageDimensions}
                type="range"
                id="dimension"
                min="0"
                max="4"
                step="1"
                value={possibleImageDimensions.findIndex(({ value }) => value === imageDimensions)}
                onChange={handleImageDimensionsChange}
              />
              <FaFileImage size={20} />
            </div>
            <p>{imageDimensions}</p>
<div className="dimension-image">
  {possibleImageDimensions.map((dimension) => {
    if (dimension.value === imageDimensions) {
      const gridTemplateColumns = `repeat(${Math.round(dimension.width / 100)}, 1fr)`;
      const gridTemplateRows = `repeat(${Math.round(dimension.height / 100)}, 1fr)`;
      const squares = Array(Math.round((dimension.width / 100) * (dimension.height / 100))).fill(null);

      return (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns,
            gridTemplateRows,
            gap: '2px',
            width: `${(dimension.width / dimension.height) * 40}%`, // Adjusted width to maintain aspect ratio
            height: '40%', // Fixed height
            backgroundColor: '#ddd',
            overflow: 'hidden',
            borderRadius: '4px',
            margin: 'auto', // Center the grid
          }}
          key={dimension.value}
        >
          {squares.map((_, index) => (
            <div
              style={{
                backgroundColor: '#888',
                opacity: index % 2 === 0 ? 0.5 : 1,
              }}
              key={index}
            />
          ))}
        </div>
      );
    }
    return null;
  })}
</div>

          </div>
