  const styleTitle = {
    myasaki: "Miyasaki Style",
    steampunk: "SteamPunk Universe",
    disney: "",
    watercolor: "",
    oilpainting: "",
    sculpture: "",
    vaporwave: "",
    digitalart: "",
    conceptart: "",
    pixar: "",
    japanesestyle: "",
    handpoked: "",
    americanoldschool: "",
    vintage: "",
  };
